function AjaxRequest(a,b){var c="";var d=Object.keys(b);for(var i=0;i<d.length;i++){var e=encodeURIComponent(b[d[i]]);c+=d[i]+"="+e+"&";};c=c.slice(0,-1);let f=new Promise((resolve,reject)=>{var g=new XMLHttpRequest();g.open("POST",a,true);g.setRequestHeader("Content-type","application/x-www-form-urlencoded");g.onreadystatechange=function(){if(g.readyState==4&&g.status==200){resolve(g.responseText);}else if(parseInt((g.status+"").slice(0,1))>=4){reject(g.status);};};g.send(c);});return f;}